CREATE TABLE attribute (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(35) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE category (id INT AUTO_INCREMENT NOT NULL, parent_id INT DEFAULT NULL, name VARCHAR(35) NOT NULL, INDEX Category_Category (parent_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE categoryattribute (Category_id INT NOT NULL, Attribute_id INT NOT NULL, INDEX IDX_63C02EE094DA1235 (Category_id), INDEX IDX_63C02EE0AED7F502 (Attribute_id), PRIMARY KEY(Category_id, Attribute_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE city (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(35) NOT NULL, Region_id INT DEFAULT NULL, INDEX City_Region (Region_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE comment (id BIGINT AUTO_INCREMENT NOT NULL, content TEXT NOT NULL, created_date DATE NOT NULL, published_date DATE NOT NULL, updated_date DATE NOT NULL, Post_id BIGINT DEFAULT NULL, User_id BIGINT DEFAULT NULL, INDEX Comment_Post (Post_id), INDEX Comment_User (User_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE favourite (id BIGINT AUTO_INCREMENT NOT NULL, Post_id BIGINT DEFAULT NULL, User_id BIGINT DEFAULT NULL, INDEX Favourite_Post (Post_id), INDEX Favourite_User (User_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE image (id INT AUTO_INCREMENT NOT NULL, url VARCHAR(255) NOT NULL, Post_id BIGINT DEFAULT NULL, INDEX Image_Post (Post_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE post (id BIGINT AUTO_INCREMENT NOT NULL, type VARCHAR(20) NOT NULL, title VARCHAR(150) NOT NULL, content TEXT NOT NULL, price DOUBLE PRECISION NOT NULL, created_date DATE NOT NULL, published_date DATE NOT NULL, updated_date DATE NOT NULL, expiration_date DATE NOT NULL, Category_id INT DEFAULT NULL, City_id INT DEFAULT NULL, Region_id INT DEFAULT NULL, User_id BIGINT DEFAULT NULL, INDEX Post_Category (Category_id), INDEX Post_City (City_id), INDEX Post_Region (Region_id), INDEX Post_User (User_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE region (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(35) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE user (id BIGINT AUTO_INCREMENT NOT NULL, username_canonical VARCHAR(180) NOT NULL, email_canonical VARCHAR(180) NOT NULL, enabled TINYINT(1) NOT NULL, salt VARCHAR(255) DEFAULT NULL, last_login DATETIME DEFAULT NULL, confirmation_token VARCHAR(180) DEFAULT NULL, password_requested_at DATETIME DEFAULT NULL, roles LONGTEXT NOT NULL COMMENT '(DC2Type:array)', firstname VARCHAR(35) NOT NULL, lastname VARCHAR(35) NOT NULL, adresse VARCHAR(255) NOT NULL, phone VARCHAR(10) NOT NULL, username VARCHAR(25) NOT NULL, email VARCHAR(60) NOT NULL, password VARCHAR(25) NOT NULL, City_id INT DEFAULT NULL, Region_id INT DEFAULT NULL, UNIQUE INDEX UNIQ_8D93D64992FC23A8 (username_canonical), UNIQUE INDEX UNIQ_8D93D649A0D96FBF (email_canonical), UNIQUE INDEX UNIQ_8D93D649C05FB297 (confirmation_token), UNIQUE INDEX UNIQ_8D93D649F85E0677 (username), UNIQUE INDEX UNIQ_8D93D649E7927C74 (email), INDEX User_City (City_id), INDEX User_Region (Region_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
